reverseCipherKey(CipherText, WantedValue)->
	CipherVal = get_char_position(CipherText),
	WantedVal = get_char_position(WantedValue),

	io:fwrite("Cipher " ++ integer_to_list(CipherVal) ++ "\n"),
	io:fwrite("Wanted " ++ integer_to_list(WantedVal) ++ "\n"),

	Change = CipherVal - WantedVal,
	if
		Change < 0 ->
			ChangeN = Change + 26;
		true ->
			ChangeN = Change
	end,
	io:fwrite("CipherKey: " ++ int_to_char(ChangeN) ++ "\n").


