loop_recursion(X, Y, Z)->
	Key = "FI" ++ int_to_char(X) ++ int_to_char(Y) ++ int_to_char(Z) ++ "L",
	Decrypted = vigenereCipher(exercise3_ciphertext(), Key),
	DoesMatch = string:str(tess26txt(), Decrypted),

	if 
		DoesMatch > 0 ->
			io:fwrite(Key);
		true ->
			true
	end,

	% io:fwrite(integer_to_list(X) ++ "/" ++ integer_to_list(Y) ++ "/" ++ integer_to_list(Z) ++ "\n"),

	if
		X < 25 ->
			loop_recursion(X+1, Y, Z);
		Y < 25 ->
			loop_recursion(0, Y+1, Z);
		Z < 25 ->
			loop_recursion(0, 0, Z+1);
		true ->
		 io:fwrite("end of loop\n")
	end.


